                    { new Guid("2c1c80f8-56df-4b2b-bb47-362ee218f494"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6464), "Trang dashboard tải rất chậm, mất hơn 10 giây để hiển thị dữ liệu.", 1, "1. Kiểm tra truy vấn SQL xem có truy vấn nặng không. \n2. Sử dụng caching để giảm tải dữ liệu truy vấn lặp lại. \n3. Tối ưu code frontend/backend để giảm thời gian tải trang.", null },
                    { new Guid("4b1eca13-b127-4564-a70d-60ccbabe15fc"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6476), "Thanh toán không thành công do lỗi ngân hàng.", 6, "1. Kiểm tra xem số dư tài khoản của khách hàng có đủ không. \n2. Hướng dẫn thử lại hoặc sử dụng phương thức thanh toán khác. \n3. Kiểm tra log giao dịch để xác minh nguyên nhân.", null },
                    { new Guid("4f06d26b-eb45-4049-bb61-9b13bf31bb0f"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6478), "Người dùng yêu cầu hoàn tiền do giao dịch lỗi.", 7, "1. Xác minh giao dịch và lý do hoàn tiền. \n2. Thực hiện hoàn tiền theo chính sách công ty. \n3. Gửi thông báo cho người dùng về trạng thái hoàn tiền.", null },
                    { new Guid("6dbca09a-7017-410b-af7c-b0e3c242698c"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6487), "Vấn đề khác không thuộc các danh mục trên.", 11, "1. Xác minh chi tiết vấn đề. \n2. Chuyển đến bộ phận phù hợp để xử lý. \n3. Phản hồi cho người dùng về cách giải quyết.", null },
                    { new Guid("86d0d783-7455-4808-b09d-f1bdee176b0d"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6484), "Người dùng yêu cầu cập nhật thông tin cá nhân (email, số điện thoại).", 10, "1. Xác minh danh tính người yêu cầu. \n2. Cập nhật thông tin theo yêu cầu nếu hợp lệ. \n3. Gửi xác nhận thay đổi cho người dùng.", null },
                    { new Guid("9fe1b731-e508-486f-96a9-d93536bc365e"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6480), "Người dùng bị trừ sai số tiền khi thanh toán.", 8, "1. Kiểm tra lịch sử giao dịch và xác minh số tiền bị trừ. \n2. Nếu lỗi thuộc về hệ thống, tiến hành hoàn tiền cho khách hàng. \n3. Sửa lỗi hệ thống nếu có để tránh tái diễn.", null },
                    { new Guid("bda4fac3-7ac0-4654-87ca-21b737ca685d"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6472), "Tài khoản bị khóa do nhập sai mật khẩu nhiều lần.", 4, "1. Kiểm tra trạng thái tài khoản trong hệ thống. \n2. Nếu bị khóa, hướng dẫn người dùng xác thực email hoặc liên hệ hỗ trợ. \n3. Cung cấp tùy chọn mở khóa tài khoản nếu hợp lệ.", null },
                    { new Guid("d0dbfd97-04ee-43f7-b0ee-99483d06a731"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6482), "Cần thêm tính năng xuất báo cáo sang Excel.", 9, "1. Ghi nhận yêu cầu và đưa vào backlog. \n2. Xác định mức độ ưu tiên của tính năng. \n3. Đưa vào kế hoạch phát triển nếu phù hợp.", null },
                    { new Guid("e27e4ca9-a954-4c9c-a1b3-c1803d1bf899"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6474), "Người dùng không thể truy cập trang quản lý mặc dù có quyền admin.", 5, "1. Kiểm tra xem quyền truy cập đã được cấp đúng chưa. \n2. Cập nhật phân quyền nếu cần thiết. \n3. Kiểm tra lại mã nguồn hoặc chính sách bảo mật.", null },
                    { new Guid("ffbf2759-037f-4f67-bd99-040b2db3222e"), new DateTime(2025, 2, 21, 1, 32, 47, 613, DateTimeKind.Local).AddTicks(6461), "Lỗi không thể thêm sản phẩm vào giỏ hàng.", 0, "1. Kiểm tra lại logic xử lý thêm sản phẩm vào giỏ hàng. \n2. Xem xét console log hoặc error log để xác định lỗi cụ thể. \n3. Cập nhật hoặc sửa lỗi trong mã nguồn nếu cần thiết.", null }
